"use strict";function togglePanelOpen(e){e.preventDefault(),this.classList.add("open")}function removeOpen(e){e.preventDefault(),this.classList.remove("open")}function toggleActive(e){e.propertyName.includes("flex")&&this.classList.toggle("open-active")}window.onload=function(){document.querySelectorAll(".panel").forEach(function(e){e.addEventListener("click",togglePanelOpen),e.addEventListener("mouseout",removeOpen),e.addEventListener("transitionend",toggleActive)})};